import React, { useState } from 'react';
import { ChevronLeftIcon, ChevronRightIcon } from '@heroicons/react/solid';
import { Button ,Modal} from 'react-bootstrap';

const Rooms = ({ room }) => {
  const [show, setShow] = useState(false);

  const handleClose = () => setShow(false);
  const handleShow = () => setShow(true);

  const [currentImageIndex, setCurrentImageIndex] = useState(0);

  const handleNextImage = () => {
    setCurrentImageIndex((prevIndex) => (prevIndex + 1) % room.imageurls.length);
  };

  const handlePrevImage = () => {
    setCurrentImageIndex((prevIndex) =>
      prevIndex === 0 ? room.imageurls.length - 1 : prevIndex - 1
    );
  };

  return (
    <div className="rounded-lg shadow-lg overflow-hidden h-80 w-80 mx-5 my-5">
      <h2 className="text-xl font-bold bg-gray-800 text-white p-4">{room.name}</h2>
      <div className="relative">
        <img src={room.imageurls[currentImageIndex]} alt="Room" className="w-full" />
        <button onClick={handlePrevImage} className="absolute top-1/2 left-2 transform -translate-y-1/2 bg-gray-800 text-white p-2 rounded-full focus:outline-none">
          <ChevronLeftIcon className="h-6 w-6" />
        </button>
        <button onClick={handleNextImage} className="absolute top-1/2 right-2 transform -translate-y-1/2 bg-gray-800 text-white p-2 rounded-full focus:outline-none">
          <ChevronRightIcon className="h-6 w-6" />
        </button>
      </div>
      <div className="p-4">
        <p className="text-lg">Rent per bed: ${room.rentperday}/month</p>
        <p className="text-lg">Type: {room.type}</p>
        <p className="text-lg">Max Person Count: {room.maxcount}</p>
        <p className="text-lg">Phone Number: {room.phonenumber}</p>
        <p className="text-lg">Total Rooms Left: {room.currrentbookings}</p>
        <button className="absolute bottom-4 right-4 px-4 py-2 bg-gray-800 text-white rounded-md hover:bg-gray-700 focus:outline-none">
          View Details
        </button>
      </div>
    
      <Button variant="primary" onClick={handleShow}>
        Modal
      </Button>

      <Modal
        show={show}
        onHide={handleClose}
        backdrop="static"
        keyboard={false}
      >
        <Modal.Header closeButton>
          <Modal.Title>Modal title</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          I will not close if you click outside me. Do not even try to press
          escape key.
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={handleClose}>
            Close
          </Button>
          <Button variant="primary">Understood</Button>
        </Modal.Footer>
      </Modal>
       
    </div>
  );
};

export default Rooms;
