import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';
import Loading from '../components/Loading';
import 'react-datepicker/dist/react-datepicker.css';
import DatePicker from 'react-datepicker';


function BookingScreen() {


  const [startDate, setStartDate] = useState(null);
  const [endDate, setEndDate] = useState(null);

  const handleStartDateChange = (date) => {
    if (!endDate || date <= endDate) {
      setStartDate(date);
    } else {
      setStartDate(date);
      setEndDate(null);
    }
  };

  const handleEndDateChange = (date) => {
    if (!startDate || date >= startDate) {
      setEndDate(date);
    } else {
      setStartDate(null);
      setEndDate(date);
    }
  };

  const { roomid } = useParams();

 
  const [Amount,setAmount]=useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [room, setRoom] = useState({});
  
  useEffect(() => {
    async function fetchData() {
      console.log(typeof startDate)
      try {
        setLoading(true);
        let data = (await axios.post("/api/rooms/getroombyid",{roomid})).data;
        setRoom(data);
        const lastDayofMonth = new Date(year, month + 1, 0).getDate();
        const month = endDate.month-startDate.month;
        if(month===0)
        {
          setAmount(Math.ceil(room.rentperday /2));
        }
        else{
          setAmount(room.rentperday * (month+1))
        }
        // console.log(room);
        setLoading(false);
      } catch (error) {
        
        console.log(error);
        setLoading(false);
        setError(error);
      }
    }
    fetchData();
  }, [startDate,endDate,roomid]);

  if (loading) {
    return <h1><Loading/></h1>;
  }

  if (error) {
    return <h1>{error.message}</h1>;
  }
  return (
   <div>
    <div className='flex justify-around'>

       <div>
         <label>From : </label>
         <DatePicker
           className='outline-dotted my-2'
           selected={startDate}
           onChange={handleStartDateChange}
           selectsStart
           startDate={startDate}
           endDate={endDate}
           minDate={Date.now()}
           dateFormat="dd/MM/yyyy"
         />
       </div>
       <div>
         <label>To : </label>
         <DatePicker
           className='outline-dotted my-2'
           selected={endDate}
           onChange={handleEndDateChange}
           selectsEnd
           startDate={startDate}
           endDate={endDate}
           minDate={startDate}
           dateFormat="dd/MM/yyyy"
         />
         </div>
      </div>
    
    <div className='flex justify-between shadow-lg p-4 rounded-lg'>
       <div className=''>
          <h1 className='m-4'>{room.name}</h1>
          <img className="h-auto w-auto" src={room.imageurls[0]} alt="room image" />
        </div>
        <div>
          <div className='relative'>
          <b>
            <p className='p-2'><span className='text-gray-900'>Description: </span>  <span className='text-gray-500'>{room.description}</span></p>
            <p className='p-2'><span className='text-gray-900'>Max Capacity: </span>  <span className='text-gray-500'>{room.maxcount}</span></p>
            <p className='p-2'><span className='text-gray-900'>Owner's Contact: </span> <span className='text-gray-500'>  {room.phonenumber}</span></p>
            <hr></hr>
            <h1 className='p-2 text-xl'>Amount</h1>
            <p className='p-2'><span className='text-gray-900'>Type: </span> <span className='text-gray-500'>{room.type}</span></p>
            <p className='p-2'><span className='text-gray-900'>Rent Per Month: Rs</span><span className='text-gray-500'>{room.rentperday}</span></p>
            <p className='p-2'><span className='text-gray-900'>Total Amount: Rs</span><span className='text-gray-500'>{Amount}</span></p>

          </b>
          <button className="absolute bottom-30 right-4 bg-blue-800 px-2 py-2 shadow-md rounded-lg text-white" type="submit">Pay Now</button>
        </div>
       </div> 
       </div>
    </div>
  );
}

export default BookingScreen;
