import React, { useState } from 'react';
import { Container, Nav, Navbar } from 'react-bootstrap';
import { useLocation, BrowserRouter as Router, Link } from 'react-router-dom';
// import logo from './logo.png'; // Assuming you have a logo file named logo.png in the same directory

function NavbarComponent() {
  const location = useLocation();
  const [darkTheme, setDarkTheme] = useState(false);

  const handleThemeToggle = () => {
    setDarkTheme(!darkTheme);
    if (darkTheme) {
      document.body.classList.remove('dark-theme');
    } else {
      document.body.classList.add('dark-theme');
    }
  };

  return (
    <>
      <Navbar className='bg-gray-800'>
        <Container className="justify-between">
          <Navbar.Brand href="/home" className='text-white'>
            <img 
              src="https://i.pinimg.com/originals/f4/fa/ec/f4faec1798f199132d27ed903701818b.png"
              className="d-inline-block align-top mr-2 h-10 w-10 rounded-lg"
              alt="PgDekho Logo"
            />
            PgDekho.com
          </Navbar.Brand>
          {location.pathname === '/home' && (
            <Nav className="d-flex flex-grow-1 justify-content-center">
              <input type="text" placeholder="Search..." className="form-control mr-2" />
            </Nav>
          )}
          <Nav className="align-items-center">
          <label className="radio-container mr-4 text-white">
            <input type="radio" name="theme" checked={!darkTheme} onChange={handleThemeToggle} />
            <span className="radio-checkmark"></span>
          </label>
          <Link to="/">
          <button className=" bg-blue-600 btn btn-primary btn-lg h-10 w-20 p-2 text-center">Logout</button>
          </Link>
        </Nav>
        </Container>
      </Navbar>
    </>
  )
}

export default NavbarComponent;
