import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';

function BookingScreen() {
  const { roomid } = useParams();

  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [room, setRoom] = useState({});

  useEffect(() => {
    async function fetchData() {
      try {
        setLoading(true);
        const data = (await axios.post("/api/rooms/getroombyid", { roomid })).data;
        setRoom(data);
        setLoading(false);
      } catch (error) {
        console.log(error);
        setLoading(false);
        setError(error);
      }
    }
    fetchData();
  }, [roomid]);

  return (
    <div className="flex justify-center items-center h-screen bg-gray-100">
      {loading && <h1>Loading...</h1>}
      {error && <h1>{error.message}</h1>}
      {!loading && !error && (
        <div className="max-w-xl w-full mx-auto bg-white shadow-lg rounded-lg p-8">
          <h1 className="text-2xl font-bold mb-4">{room.name}</h1>
          <div className="mb-4">
            <img className="w-full h-auto rounded-lg" src={room.imageurls[0]} alt="Room" />
          </div>
          <div className="text-gray-600">
            <p className="mb-2"><strong>Description:</strong> {room.description}</p>
            <p className="mb-2"><strong>Max Capacity:</strong> {room.maxcount}</p>
            <p className="mb-2"><strong>Type:</strong> {room.type}</p>
            <p className="mb-2"><strong>Owner's Contact:</strong> {room.phonenumber}</p>
            <p className="mb-2"><strong>Rent Per Month:</strong> Rs {room.rentperday}</p>
          </div>
          <button className="absolute bottom-4 right-4 bg-blue-500 text-white px-4 py-2 rounded-lg shadow-md">Pay Now</button>
        </div>
      )}
    </div>
  );
}

export default BookingScreen
