import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';

function BookingScreen() {
  const { roomid } = useParams();

  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [room, setRoom] = useState({});

  useEffect(() => {
    async function fetchData() {
      try {
        setLoading(true);
        let data = (await axios.post("/api/rooms/getroombyid",{roomid})).data;
        setRoom(data);
        setLoading(false);
    } catch (error) {
        
        console.log(error);
        setLoading(false);
        setError(error);
      }
    }
    fetchData();
  }, []);

  if (loading) {
    return <h1>Loading...</h1>;
  }

  if (error) {
    return <h1>{error.message}</h1>;
  }
  return (
    <div className='flex justify-between'>
       <div className='m-4'>
          <h1>{room.name}</h1>
          <img className="h-auto w-auto" src={room.imageurls[0]} alt="room image" />
        </div>
        <div >
          <p className='p-4'><span className='text-gray-900'>Description:</span> {room.description}</p>
          <p className='p-4'><span className='text-gray-800'>Max Capacity:</span>{room.maxcount}</p>
          <p className='p-4'><span className='text-gray-800'>Type: </span> {room.type}</p>
          <p className='p-4'><span className='text-gray-800'>Owner's Contact: </span>{room.phonenumber}</p>
          <p className='p-4'><span className='text-gray-800'>Rent Per Month: Rs</span>{room.rentperday}</p>
       </div> 
    </div>
  );
}

export default BookingScreen;
