import React, { useState } from 'react';
import { ChevronLeftIcon, ChevronRightIcon } from '@heroicons/react/solid';

const Rooms = ({ room }) => {
  const [currentImageIndex, setCurrentImageIndex] = useState(0);

  const handleNextImage = () => {
    setCurrentImageIndex((prevIndex) => (prevIndex + 1) % room.images.length);
  };

  const handlePrevImage = () => {
    setCurrentImageIndex((prevIndex) =>
      prevIndex === 0 ? room.images.length - 1 : prevIndex - 1
    );
  };

  return (
    <div className="rounded-lg shadow-lg overflow-hidden">
      <h2 className="text-xl font-bold bg-gray-800 text-white p-4">{room.name}</h2>
      <div className="relative">
        <img src={room.images[currentImageIndex]} alt="Room" className="w-full" />
        <button onClick={handlePrevImage} className="absolute top-1/2 left-2 transform -translate-y-1/2 bg-gray-800 text-white p-2 rounded-full focus:outline-none">
          <ChevronLeftIcon className="h-6 w-6" />
        </button>
        <button onClick={handleNextImage} className="absolute top-1/2 right-2 transform -translate-y-1/2 bg-gray-800 text-white p-2 rounded-full focus:outline-none">
          <ChevronRightIcon className="h-6 w-6" />
        </button>
      </div>
      <div className="p-4">
        <p className="text-lg">Rent per bed: ${room.rentPerBed}/month</p>
        <p className="text-lg">Type: {room.type}</p>
        <p className="text-lg">Max Person Count: {room.maxPersonCount}</p>
        <p className="text-lg">Phone Number: {room.phoneNumber}</p>
        <p className="text-lg">Total Rooms Left: {room.totalRoomsLeft}</p>
        <button className="absolute bottom-4 right-4 px-4 py-2 bg-gray-800 text-white rounded-md hover:bg-gray-700 focus:outline-none">
          View Details
        </button>
      </div>
    </div>
  );
};

export default Rooms;
