import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';
import { Button } from 'react-bootstrap';
function BookingScreen() {
  const { roomid } = useParams();

  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [room, setRoom] = useState({});

  useEffect(() => {
    async function fetchData() {
      try {
        setLoading(true);
        let data = (await axios.post("/api/rooms/getroombyid",{roomid})).data;
        setRoom(data);
        setLoading(false);
    } catch (error) {
        
        console.log(error);
        setLoading(false);
        setError(error);
      }
    }
    fetchData();
  }, []);

  if (loading) {
    return <h1>Loading...</h1>;
  }

  if (error) {
    return <h1>{error.message}</h1>;
  }
  return (
    <div className='flex justify-between shadow-lg m-4 rounded-lg'>
       <div className='m-4'>
          <h1 className='m-4'>{room.name}</h1>
          <img className="h-auto w-auto" src={room.imageurls[0]} alt="room image" />
        </div>
        <div >
        <b>
          <p className='p-4'><span className='text-gray-900'>Description: </span>  <span className='text-gray-500'>{room.description}</span></p>
          <p className='p-4'><span className='text-gray-900'>Max Capacity: </span>  <span className='text-gray-500'>{room.maxcount}</span></p>
          <p className='p-4'><span className='text-gray-900'>Type: </span> <span className='text-gray-500'>{room.type}</span></p>
          <p className='p-4'><span className='text-gray-900'>Owner's Contact: </span> <span className='text-gray-500'>  {room.phonenumber}</span></p>
          <p className='p-4'><span className='text-gray-900'>Rent Per Month: Rs</span><span className='text-gray-500'>{room.rentperday}</span></p>
        </b>
        <button className="right-0 background-blue-700" type="submit">Pay Now</button>
       </div> 

    </div>
  );
}

export default BookingScreen;
