import React, { useState } from 'react';
import { Container, Nav, Navbar } from 'react-bootstrap';
import { useLocation, BrowserRouter as Router } from 'react-router-dom';
// import logo from './logo.png'; // Assuming you have a logo file named logo.png in the same directory

function NavbarComponent() {
  const location = useLocation();
  const [darkTheme, setDarkTheme] = useState(false);

  const handleThemeToggle = () => {
    setDarkTheme(!darkTheme);
    // You can add logic to toggle dark/light theme here
  };

  return (
    <>
      <Navbar bg={darkTheme ? "dark" : "light"} variant={darkTheme ? "dark" : "light"} expand="lg">
        <Container className="justify-content-between">
          <Navbar.Brand href="/home">
            <img 
              src="https://i.pinimg.com/originals/f4/fa/ec/f4faec1798f199132d27ed903701818b.png"
              className="d-inline-block align-top mr-2 h-10 w-10 rounded-2xl"
              alt="PgDekho Logo"
            />
            PgDekho.com
          </Navbar.Brand>
          {location.pathname === '/home' && (
            <Nav className="d-flex flex-grow-1 justify-content-center">
              <input type="text" placeholder="Search..." className="form-control mr-2" />
            </Nav>
          )}
          <Nav>
            <label className="radio-container mr-2">
              <input type="radio" name="theme" checked={!darkTheme} onChange={handleThemeToggle} />
              <span className="radio-checkmark"></span>
            </label>
            <Nav.Link href="/">Logout</Nav.Link>
          </Nav>
        </Container>
      </Navbar>
    </>
  )
}

export default NavbarComponent;
