import React, { useState } from 'react';
import { ChevronLeftIcon, ChevronRightIcon } from '@heroicons/react/solid';
import { Button, Modal } from 'react-bootstrap';

const Rooms = ({ room }) => {
  const [show, setShow] = useState(false);
  const handleClose = () => setShow(false);
  const handleShow = () => setShow(true);
  const [currentImageIndex, setCurrentImageIndex] = useState(0);

  const handleNextImage = () => {
    setCurrentImageIndex((prevIndex) => (prevIndex + 1) % room.imageurls.length);
  };

  const handlePrevImage = () => {
    setCurrentImageIndex((prevIndex) =>
      prevIndex === 0 ? room.imageurls.length - 1 : prevIndex - 1
    );
  };

  return (
    <div className="flex flex-col rounded-lg shadow-lg overflow-hidden w-72 h-96 mx-2 my-2">
      <h2 className="text-lg font-bold bg-gray-800 text-white p-2 truncate">{room.name}</h2>
      <div className="relative flex-grow">
        <img src={room.imageurls[currentImageIndex]} alt="Room" className="w-full h-full object-cover" />
        <button onClick={handlePrevImage} className="absolute top-1/2 left-2 transform -translate-y-1/2 bg-gray-800 text-white p-2 rounded-full focus:outline-none">
          <ChevronLeftIcon className="h-6 w-6" />
        </button>
        <button onClick={handleNextImage} className="absolute top-1/2 right-2 transform -translate-y-1/2 bg-gray-800 text-white p-2 rounded-full focus:outline-none">
          <ChevronRightIcon className="h-6 w-6" />
        </button>
      </div>
      <div className="p-2 flex justify-between items-center">
        <p className="text-sm">Rent: ${room.rentperday}/month</p>
        <button onClick={handleShow} className="text-sm bg-gray-800 text-white rounded-md px-2 py-1 focus:outline-none">
          View Details
        </button>
      </div>
      <Modal
        show={show}
        onHide={handleClose}
        backdrop="static"
        keyboard={false}
      >
        <Modal.Header closeButton>
          <Modal.Title>{room.name} Details</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <p>Rent per bed: ${room.rentperday}/month</p>
          <p>Type: {room.type}</p>
          <p>Max Person Count: {room.maxcount}</p>
          <p>Phone Number: {room.phonenumber}</p>
          <p>Total Rooms Left: {room.currrentbookings}</p>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={handleClose}>
            Close
          </Button>
        </Modal.Footer>
      </Modal>
    </div>
  );
};

export default Rooms;
