import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';

function BookingScreen({match}) {
  const { roomid } = useParams();
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [room, setRoom] = useState(null);

  useEffect(() => {
    async function fetchData() {
      try {
        setLoading(true);
        const { data } = await axios.post("api/rooms/getroombyid", { roomid:match.params.roomid});
        setRoom(data);
        setLoading(false);
      } catch (error) {
        setLoading(false);
        setError(error);
      }
    }
    fetchData();
  }, [roomid]); // Include roomid as a dependency to fetch data whenever it changes

  if (loading) {
    return <h1>Loading...</h1>;
  }

  if (error) {
    return <h1>{error.message}</h1>;
  }

  // Render content only if room is available
  return (
    <div className='flex flex-col'>
      <div className='l-0'>
        <h1>{room.name}</h1>
        <img className="bigimg" src={room.imageurls[0]} alt="room image" />
      </div>
      <div className='r-0'>
        <p>Description: {room.description}</p>
        <p>Max Capacity: {room.maxcount}</p>
        <p>Type: {room.type}</p>
        <p>Owner's Contact: {room.phonenumber}</p>
        <p>Rent Per Month: Rs {room.rentperday}</p>
      </div>
    </div>
  );
}

export default BookingScreen;
